    const totalResponPelaku = pelakuSangatTinggi + pelakuTinggi + pelakuSedang + pelakuRendah;

    if(totalResponKorban != 0 || totalResponPelaku != 0){
        const persenKorbanSangatTinggi = (korbanSangatTinggi / totalResponKorban) * 100;
